import React from 'react';
import './App.css';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      inputValue: '',
      tasks: []
    }
    this.handleChange = this.handleChange.bind(this);
    this.handleClick = this.handleClick.bind(this);
    this.handleClear = this.handleClear.bind(this);
  }

  handleChange(e) {
    this.setState({inputValue: e.target.value});
  }

  handleClick(e) {
    e.preventDefault();
    const newTasks = [...this.state.tasks];
    newTasks.unshift(this.state.inputValue);
    this.setState({inputValue: '',tasks: newTasks});
    const storeString = JSON.stringify(newTasks);
    localStorage.setItem('tasksHistory', storeString)
  }

  deleteItem(index) {
    const items = [...this.state.tasks]
    items.splice(index,1)
    this.setState({tasks: items})
    const itemsString = JSON.stringify(items);
    localStorage.setItem('tasksHistory', itemsString)
  }

  componentDidMount() {
    const storage = localStorage.getItem('tasksHistory');
    const storageArray = JSON.parse(storage) || [];
    this.setState({tasks: storageArray})
  }

  handleClear() {
    this.setState({tasks: []})
    localStorage.clear('tasksHistory');
  }


  render() {
    return (
      <div className="jumbotron">
        <div className="container">
          <h1 className="display-4">Your "To-Do" List</h1>
          <input type="text" value={this.state.inputValue} onChange={this.handleChange} placeholder="Task" />
          <button type="submit" onClick={this.handleClick}>ADD</button>
            {this.state.tasks.map((item, index) =>{
              return (
                  <div key={index}>
                    <hr className="my-4" />
                    <input type="checkbox" />
                    {item}
                    <button type="submit" onClick={this.deleteItem.bind(this,index)}>REMOVE</button></div>   
              )
            })}
            {this.state.tasks.length !==0 ? 
            <button type="submit" onClick={this.handleClear}>CLEAR LIST</button>
            : null}
        </div>
      </div>
    )
  }
}

export default App;
